def find_start_of_packet(buffer):
  recent_chars = [] # list to store the four most recent characters
  for i in range(len(buffer)):
    # Add the current character to the list of recent characters
    recent_chars.append(buffer[i])
    # If the list has more than four characters, remove the oldest one
    if len(recent_chars) > 4:
      recent_chars.pop(0)
    # If the list has no duplicates, return the index of the current character
    if len(set(recent_chars)) == 4:
      return i + 1
  # If no start-of-packet marker was found, return -1
  return -1

# test the solution
print(find_start_of_packet(""))  # should print the number of characters processed before the marker is detected
